import React from 'react'
import styled from 'styled-components'
import styles from '../styles'

const IntroBox = () => (
  <Container>
    <Box>
      <Image />
      <Content>
        <ContentContainer>
          <Text style={{'fontSize': '30px', 'margin-bottom': '10px'}}>Hi, I am Sam</Text>
          <Text style={{'fontWeight': '800', 'margin-bottom': '20px'}}>Second Year Computer Science Undergraduate</Text>
          <Text style={{'fontWeight': '800', 'margin-bottom': '5px'}}>Email:</Text>
          <Text>lau.siawsam.u.nus.edu</Text>
        </ContentContainer>
      </Content>
    </Box>
  </Container>
)

export default IntroBox

const Container = styled.section`
  width: 100%;
  border: 1px solid black;
`

const Box = styled.div`
  height: 45vh;
  width: 50%;
  margin: auto;
  margin-top: 10vh;

  display: flex;
  border: 1px solid black;
`

const Image = styled.div`
  width: 50%;
`

const Content = styled.div`
  width: 50%;

  background-color: #F1F1F1;
  color: ${styles.PRI_COLOR};

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

const ContentContainer = styled.div`
  display: flex;
  flex-direction: column;
`

const Text = styled.p`
  margin: 0;
  font-size: 14px;
  font-weight: 400;
`

const ResumeButton = styled.button`
  background-color: ${styles.PRI_COLOR};
  width: 40px;
`
