import React from 'react'
import styled from 'styled-components'
import SectionHeading from './SectionHeading'
import Text from './Text'

const Education = () => (
  <Container>
    <SectionHeading style={{ marginBottom: '60px' }}>Experience</SectionHeading>

    <Body>
      <ExperienceContainer>
        <LeftSection>
          <Text style={{ fontSize: '18px', 'margin-bottom': '10px' }}>August 2019 - Present</Text>
          <Text style={{ fontSize: '20px', fontWeight: '800' }}>Bachelor of Computing (Honours) in Computer Science</Text>
        </LeftSection>

        <VerticalDivider />

        <RightSection>
          <Text style={{ fontSize: '23px' }}>National University of Singapore</Text>
          <ul>
            <ListItem>
              Dean's List (top 5%) in Fall 2020
            </ListItem>
            <ListItem>
              Cumulative Average Point: 4.89/5.00
            </ListItem>
            <ListItem>
              On track to First Class Honours
            </ListItem>
            <ListItem>
              Graduating in May 2023
            </ListItem>

          </ul>
        </RightSection>
      </ExperienceContainer>

    </Body>

  </Container>

)

export default Education

const Container = styled.section`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const Body = styled.div`
  display: flex;
  flex-direction: column;
  width: 70%;
`

const ExperienceContainer = styled.div`
  display: flex;
  padding-left: 10%;
`

const LeftSection = styled.div`
  width: 30%;
  display: flex;
  flex-direction: column;
  margin-right: 50px;
  padding-top: 50px;
`

const VerticalDivider = styled.div`
  width: 1px;
  background-color: #B0B0B0;
`

const RightSection = styled.div`
  width: 60%;
  display: flex;
  flex-direction: column;
  margin-left: 50px;
  padding-top: 50px;
`

const ListItem = styled.li`
  font-size: 18px;
`

const ListText = styled(Text)`
  line-height: 26px;
`
