import React from 'react'
import styled from 'styled-components'
import styles from '../styles'
import SectionHeading from './SectionHeading'
import Text from './Text'

const LANGUAGES = ['Javascript', 'Java', 'C', 'Python', 'SQL']
const FRONTEND = ['ReactJS', 'React Native', 'Redux']
const BACKEND = ['NodeJS', 'ExpressJS', 'MongoDB', 'PostgreSQL', 'GraphQL']
const CREATIVE = ['Adobe Photoshop Lightroom', 'Adobe Photoshop']

const Skills = () => (
  <Container>
    <SectionHeading style={{ color: 'inherit' }}>
      Skills
    </SectionHeading>

    <Body>
      <Column style={{ paddingRight: '20%' }}>

        <ColumnHeading>
          Programming
        </ColumnHeading>

        <ColumnSubheading>
          Languages
        </ColumnSubheading>
        <ul>
          {LANGUAGES.map((l) => (
            <ListItem>
              <ListText>
                {l}
              </ListText>
            </ListItem>
          ))}
        </ul>

        <ColumnSubheading>
          Frontend Development
        </ColumnSubheading>
        <ul>
          {FRONTEND.map((l) => (
            <ListItem>
              <ListText>
                {l}
              </ListText>
            </ListItem>
          ))}
        </ul>

        <ColumnSubheading>
          Backend Development
        </ColumnSubheading>
        <ul>
          {BACKEND.map((l) => (
            <ListItem>
              <ListText>
                {l}
              </ListText>
            </ListItem>
          ))}
        </ul>

      </Column>

      <Column>
        <ColumnHeading>
          Creative
        </ColumnHeading>

        <ul style={{ margin: '0 0' }}>
          {CREATIVE.map((l) => (
            <ListItem>
              <ListText>
                {l}
              </ListText>
            </ListItem>
          ))}
        </ul>

      </Column>

    </Body>
  </Container>

)

export default Skills

const Container = styled.section`
  width: 100%;
  background-color: ${styles.SKILLS_COLOR};
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const Body = styled.div`
  display: flex;
  width: 40%;
  justify-content: center;
`

const Column = styled.div`
  width: 40%;
  display: flex;
  flex-direction: column;
`

const ColumnHeading = styled(Text)`
  color: white;
  margin-bottom: 30px;
  font-size: 20px;
  font-weight: 600;
  text-align: center;
`

const ColumnSubheading = styled(Text)`
  color: white;
  font-weight: 600;
`

const ListItem = styled.li`
  font-size: 18px;
`

const ListText = styled(Text)`
  line-height: 26px;
  color: white;
`
