import React from 'react'
import styled from 'styled-components'
import styles from '../styles'
import SectionHeading from './SectionHeading'
import Text from './Text'

const LANGUAGES = ['Javascript', 'Java', 'C', 'Python', 'SQL']
const FRONTEND = ['ReactJS', 'React Native', 'Redux']
const BACKEND = ['NodeJS', 'ExpressJS', 'MongoDB', 'PostgreSQL', 'GraphQL']
const CREATIVE = ['Adobe Photoshop Lightroom', 'Adobe Photoshop']

const Skills = () => (
  <Container>
    <SectionHeading style={{ color: 'inherit' }}>
      Skills
    </SectionHeading>

    <Body>
      <Column>
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <ColumnHeading>
            Programming
          </ColumnHeading>
          <ColumnSubheading>
            Languages
          </ColumnSubheading>
          <ul>
            {LANGUAGES.map((l) => (
              <ListItem>
                <ListText>
                  {l}
                </ListText>
              </ListItem>
            ))}
          </ul>
        </div>
      </Column>

      <Column>
        <ColumnHeading>
          Creative
        </ColumnHeading>
      </Column>

    </Body>
  </Container>

)

export default Skills

const Container = styled.section`
  width: 100%;
  background-color: ${styles.SKILLS_COLOR};
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const Body = styled.div`
  display: flex;
  width: 70%;
`

const Column = styled.div`
  width: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const ColumnHeading = styled(Text)`
  color: white;
  margin-bottom: 30px;
`

const ColumnSubheading = styled(Text)`
  color: white;
`

const ListItem = styled.li`
  font-size: 18px;
`

const ListText = styled(Text)`
  line-height: 26px;
  color: white;
`
