import React from 'react'
import styled from 'styled-components'
import Text from './Text'
import SquareButton from './SquareButton'
import styles from '../styles'

const COPY = [
  "Lox is a programming language introduced in Bob Nystrom's Crafting Interpreters",
  'Inspired by the book, I am writing two interpreters for Lox: jlox & clox',
  'jlox is a tree-walk interpreter written in Java and uses a recursive descent parser',
  'clox is a bytecode interpreter written in C and compiles Lox source code into bytecode before executing it on its own virtual machine',
]

const Project = () => (
  <Container>
    <ProjectTitle>LOX</ProjectTitle>
    <Body>
      <Text>JANUARY 2021</Text>
      <VerticalDivider />

      <Content>
        <ul style={{ margin: '0' }}>
          {COPY.map((copy) => (
            <ListItem>
              <ListText>
                {copy}
              </ListText>
            </ListItem>
          ))}
        </ul>

        <ButtonContainer>
          <SquareButton
            text="View jlox"
            href="https://github.com/Silvernitro/jlox"
          />
        </ButtonContainer>
      </Content>
    </Body>

  </Container>
)

export default Project

const Container = styled.section`
  width: 60%;
  display: flex;
  flex-direction: column;
`

const ProjectTitle = styled.h2`
  font-size: 25px;
  font-weight: 100;
  color: ${styles.PRI_COLOR};
`

const Body = styled.div`
  width: 100%;
  display: flex;
`

const VerticalDivider = styled.div`
  background-color: #2F2E2E;
  width: 3px;
  margin-left: 40px;
`

const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: start;
`

const ListItem = styled.li`
  font-size: 18px;
`

const ListText = styled(Text)`
  line-height: 26px;
`

const ButtonContainer = styled.div`
  display: flex;
  margin-top: 20px;
  margin-left: 40px;
`
