import React from 'react'
import styled from 'styled-components'
import styles from '../styles'

const IntroBox = () => (
  <Container>
    <Box>
      <Image />
      <Content>
        <Text style={{'fontSize': '30px', 'fontWeight': '400'}}>Hi, I am Sam</Text>
        <Text style={{'fontWeight': '400'}}>Second Year Computer Science Undergraduate</Text>

      </Content>
    </Box>
  </Container>
)

export default IntroBox

const Container = styled.section`
  width: 100%;
  border: 1px solid black;
`

const Box = styled.div`
  height: 45vh;
  width: 50%;
  margin: auto;
  margin-top: 10vh;

  display: flex;
  border: 1px solid black;
`

const Image = styled.div`
  width: 50%;
`

const Content = styled.div`
  width: 50%;
  background-color: #F1F1F1;
  color: ${styles.PRI_COLOR};
`

const Text = styled.p`
  margin: 0;
`
