import React from 'react'
import styled from 'styled-components'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import styles from '../styles'
import headshot from '../assets/headshot.webp'

const IntroBox = () => (
  <Container>
    <Box>
      <Image src={headshot} />

      <VerticalDivider />

      <Content>
        <ContentContainer>
          <Text style={{'fontSize': '30px', 'marginBottom': '10px'}}>Hi, I am Sam</Text>
          <Text style={{'fontWeight': '800', 'marginBottom': '30px'}}>Second Year Computer Science Undergraduate</Text>
          <Text style={{'fontWeight': '800', 'marginBottom': '10px'}}>Email:</Text>
          <Text style={{'letterSpacing': '0.05em'}}>lau.siawsam.u.nus.edu</Text>

          <ResumeButton>My Resume</ResumeButton>

          <SocialContainer>
            <StyledIcon icon={['fab', 'linkedin']} />
            <StyledIcon icon={['fab', 'github']} />
            <StyledIcon icon={['fab', 'telegram']} />
          </SocialContainer>
        </ContentContainer>
      </Content>
    </Box>
  </Container>
)

export default IntroBox

const Container = styled.section`
  width: 100%;
`

const Box = styled.div`
  margin: auto;
  margin-top: 10vh;

  display: flex;
`

const Image = styled.img`
  width: 470px;
  object-fit: contain;
`

const VerticalDivider = styled.div`
  width: 2px;
`

const Content = styled.div`
  width: 470px%;
  max-width: 50%;
  padding: 80px 0px;

  background-color: #F1F1F1;
  color: ${styles.PRI_COLOR};

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

const ContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: start;
`

const Text = styled.p`
  margin: 0;
  font-size: 14px;
  font-weight: 400;
`

const ResumeButton = styled.button`
  background-color: ${styles.PRI_COLOR};

  border: none;
  color: white;
  font-weight: 50;
  letter-spacing: 0.05em;

  display: flex;
  justify-content: center;
  padding: 10px 35px;
  margin-top: 80px;
`

const SocialContainer = styled.div`
  margin-top: 50px;
`

const StyledIcon = styled(FontAwesomeIcon)`
  color: black;
  font-size: 30px;
  margin-right: 15px;
`
